package Edurekha;

public class ArithmeticOperatorDemo {

	
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		 int num1 = 75;
	      int num2 = 26;

	      System.out.println("num1 + num2: " + (num1 + num2) );
	      System.out.println("num1 - num2: " + (num1 - num2) );
	      System.out.println("num1 * num2: " + (num1 * num2) ); 
	      System.out.println("num1 / num2: " + (num1 / num2) );
	      System.out.println("num1 % num2: " + (num1 % num2) );

	}

}
