package JavaProject2018;

public class FirstCode {

		public static void main(String[] args) {
		// TODO Auto-generated method stub
			System.out.println("Hello Fokes this is is Data Types");
			
			long l=5l; //8 bytes We have to specify L
			System.out.println(l+" This is long Data Type having 8 Bytes");
			int i=5;  //4 bytes ->32 bites
			System.out.println(i+" This is int Data Type having 4 Bytes ");
			short s=5; //2 bytes ->16 bites ->-32768 to 32767
			System.out.println(s+" This is short s Data Type having 2 Bytes ");
			byte b=5; //1 bytes ->8 bites -> -128 to 127
			System.out.println(b+" This is byte Data Type having 1 Bytes");
			float f=5.5f;
			System.out.println(f+" This is float Data Type having 8 Bytes");
			double d=5.5;
			System.out.println(d+" This is double Data Type having 8 Bytes");
			char c='A';
			System.out.println(c+" This is char Data Type having True and False value");
			

	}

}
