package ProgramsforJava;

public class Assigmnt_Incrmnt_Operatr {

		public static void main(String[] args) {
		// TODO Auto-generated method stub
			int x=10,y=20;
			x=x+1;
			
			//Post Incrment
			System.out.println(x++);
			System.out.println(x);
			//PreIncrment
			System.out.println(++x);
			System.out.println(x);
			//Assignment Operator
			y+=5;// y=y+5 both are same 	
			System.out.println(y);
			
			

	}

}
