package ProgramsforJava;
public class MyFristProgram {

	/**
	 * byte(number,1 byte)
	 * short(number, 2 byte)
	 * Int(number, 4 byte)
	 * long(number,8 byte)
	 * float(Float number, 4 byte)
	 * double(float number, 8 byte)
	 * char(a character, 2 byte)
	 * boolean(true,false,1 byte)
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		short my_variable=10;
		float my_decimaal=(float)4.5;
		double my_double=10.5;
		char my_char= 'a';
		boolean is_true=true;
		System.out.println(my_variable);
		System.out.println(my_decimaal);
		System.out.println(my_double);
		System.out.println(my_char);
		System.out.println(is_true);

	}

}
